import React from "react";

export default function Item({ item, onDeleteItem, onToggleItem, onEditItem }) {
  return (
    <li className="flex items-center justify-between py-2 border-b border-gray-300">
      <div className="flex items-center">
        <input
          type="checkbox"
          checked={item.packed}
          onChange={() => onToggleItem(item.id)}
          className="mr-4"
        />
        <span className={`flex-1 ${item.packed ? "line-through" : ""}`}>
          {item.description} (x{item.quantity}) - {item.category}
          {item.dueDate && ` (Due: ${item.dueDate})`}
        </span>
      </div>
      <div className="flex items-center space-x-2">
        <button
          className="py-1 px-3 bg-yellow-500 text-white rounded-lg hover:bg-yellow-600"
          onClick={() =>
            onEditItem(item.id, {
              ...item,
              description: prompt("Edit description", item.description),
            })
          }
        >
          Edit
        </button>
        <button
          className="py-1 px-3 bg-red-500 text-white rounded-lg hover:bg-red-600"
          onClick={() => onDeleteItem(item.id)}
        >
          Delete
        </button>
      </div>
    </li>
  );
}
