import { useState } from "react";

export default function Form({ onAddItems, categories, onAddCategory }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [category, setCategory] = useState("General");
  const [dueDate, setDueDate] = useState("");
  const [newCategory, setNewCategory] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    if (!description) return;

    const newItem = {
      description,
      quantity,
      packed: false,
      id: Date.now(),
      category,
      dueDate,
    };

    onAddItems(newItem);
    setDescription("");
    setQuantity(1);
    setCategory("General");
    setDueDate("");
  }

  function handleAddCategory(e) {
    e.preventDefault();
    if (!newCategory) return;
    onAddCategory(newCategory);
    setNewCategory("");
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòç trip?</h3>
      <select
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
        className="w-full py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        className="w-full py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
      />
      <select
        value={category}
        onChange={(e) => setCategory(e.target.value)}
        className="w-full py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
      >
        {categories.map((cat) => (
          <option value={cat} key={cat}>
            {cat}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="New category..."
        value={newCategory}
        onChange={(e) => setNewCategory(e.target.value)}
        className="w-full py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
      />
      <button
        className="w-full py-2 px-4 bg-blue-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-blue-600 mb-4"
        onClick={handleAddCategory}
      >
        Add Category
      </button>
      <input
        type="date"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
        className="w-full py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
      />
      <button
        type="submit"
        className="w-full py-2 px-4 bg-green-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-green-600"
      >
        Add Item
      </button>
    </form>
  );
}
