export default function PackingList({
  items,
  onDeleteItem,
  onToggleItem,
  onEditItem,
  onClearList,
}) {
  const groupedItems = items.reduce((acc, item) => {
    if (!acc[item.category]) {
      acc[item.category] = [];
    }
    acc[item.category].push(item);
    return acc;
  }, {});

  return (
    <div className="packing-list w-full max-w-xl bg-white p-8 rounded-lg shadow-md mt-4">
      {Object.keys(groupedItems).map((category) => (
        <div key={category} className="mb-6">
          <h4 className="text-lg font-semibold mb-2">{category}</h4>
          <ul>
            {groupedItems[category].map((item) => (
              <li
                key={item.id}
                className="flex items-center justify-between mb-2"
              >
                <span
                  className={`flex-1 cursor-pointer ${
                    item.packed ? "line-through text-gray-500" : ""
                  }`}
                  onClick={() => onToggleItem(item.id)}
                >
                  {item.quantity} x {item.description}
                </span>
                <span className="flex items-center">
                  <button
                    className="py-1 px-2 bg-yellow-500 text-white rounded-lg mr-2 transition duration-300 hover:bg-yellow-600"
                    onClick={() =>
                      onEditItem(item.id, { packed: !item.packed })
                    }
                  >
                    Edit
                  </button>
                  <button
                    className="py-1 px-2 bg-red-500 text-white rounded-lg transition duration-300 hover:bg-red-600"
                    onClick={() => onDeleteItem(item.id)}
                  >
                    Delete
                  </button>
                </span>
              </li>
            ))}
          </ul>
        </div>
      ))}
      <button
        className="w-full py-2 px-4 bg-red-500 text-white rounded-lg transition duration-300 hover:bg-red-600 mt-4"
        onClick={onClearList}
      >
        Clear List
      </button>
    </div>
  );
}
