import React from "react";

export default function PackingList({
  items,
  selectedCategory,
  onDeleteItem,
  onToggleItem,
  onEditItem,
  onClearList,
}) {
  const filteredItems =
    selectedCategory === "All"
      ? items
      : items.filter((item) => item.category === selectedCategory);

  return (
    <div className="packing-list w-full max-w-xl bg-white p-8 rounded-lg shadow-md">
      <ul>
        {filteredItems.map((item) => (
          <li key={item.id} className="flex justify-between items-center mb-2">
            <span className={item.packed ? "line-through" : ""}>
              {item.description} ({item.quantity})
            </span>
            <div>
              <button
                onClick={() => onToggleItem(item.id)}
                className="px-2 py-1 bg-green-500 text-white rounded-lg mr-2"
              >
                {item.packed ? "Unpack" : "Pack"}
              </button>
              <button
                onClick={() => onDeleteItem(item.id)}
                className="px-2 py-1 bg-red-500 text-white rounded-lg"
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
      <button
        onClick={onClearList}
        className="w-full py-2 px-4 bg-red-500 text-white rounded-lg mt-4"
      >
        Clear All
      </button>
    </div>
  );
}
