import React from "react";
import PackingItem from "./PackingItem";

export default function PackingList({
  items,
  selectedCategory,
  onDeleteItem,
  onToggleItem,
  onEditItem,
  onClearList,
}) {
  const filteredItems =
    selectedCategory === "All"
      ? items
      : items.filter((item) => item.category === selectedCategory);

  return (
    <div className="packing-list">
      <h3 className="text-xl text-gray-700 mb-4">Packing List</h3>
      <ul>
        {filteredItems.map((item) => (
          <PackingItem
            key={item.id}
            item={item}
            onDeleteItem={onDeleteItem}
            onToggleItem={onToggleItem}
            onEditItem={onEditItem}
          />
        ))}
      </ul>
      {filteredItems.length > 0 && (
        <button
          className="mt-4 py-2 px-4 bg-red-500 text-white rounded-lg cursor-pointer transition duration-300 hover:bg-red-600"
          onClick={onClearList}
        >
          Clear List
        </button>
      )}
    </div>
  );
}
