import React from "react";

export default function PackingList({
  items,
  categories,
  onDeleteItem,
  onToggleItem,
  onEditItem,
  onClearList,
}) {
  return (
    <div className="packing-list w-full max-w-3xl bg-white p-8 rounded-lg shadow-lg mt-8">
      <h2 className="text-2xl text-gray-700 mb-4">Your Packing List</h2>
      <ul className="list-none p-0">
        {items.map((item) => (
          <li
            key={item.id}
            className="flex justify-between items-center py-2 border-b border-gray-200"
          >
            <div className="flex items-center">
              <input
                type="checkbox"
                className="mr-2"
                checked={item.packed}
                onChange={() => onToggleItem(item.id)}
              />
              <span
                className={`${
                  item.packed ? "line-through text-gray-500" : "text-gray-900"
                }`}
              >
                {item.description} ({item.quantity}) - {item.category} -{" "}
                {item.dueDate}
              </span>
            </div>
            <button
              className="py-1 px-2 bg-red-500 text-white rounded-lg transition duration-300 hover:bg-red-600"
              onClick={() => onDeleteItem(item.id)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
      {items.length > 0 && (
        <button
          className="mt-4 py-2 px-4 bg-red-500 text-white rounded-lg transition duration-300 hover:bg-red-600"
          onClick={onClearList}
        >
          Clear List
        </button>
      )}
    </div>
  );
}
