export default function PackingList({
  items,
  selectedCategory,
  onDeleteItem,
  onToggleItem,
  onEditItem,
  onClearList,
}) {
  const filteredItems =
    selectedCategory === "All"
      ? items
      : items.filter((item) => item.category === selectedCategory);

  return (
    <div className="packing-list">
      <ul className="list-none p-0">
        {filteredItems.map((item) => (
          <li key={item.id} className="p-4 mb-2 bg-white rounded shadow">
            <input
              type="checkbox"
              checked={item.packed}
              onChange={() => onToggleItem(item.id)}
              className="mr-2"
            />
            <span className={item.packed ? "line-through" : ""}>
              {item.quantity} {item.description} ({item.category})
            </span>
            <button
              onClick={() => onDeleteItem(item.id)}
              className="ml-4 text-red-500"
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
      {filteredItems.length === 0 && (
        <p className="text-center text-gray-500">No items in this category.</p>
      )}
      <button
        onClick={onClearList}
        className="mt-4 py-2 px-4 bg-red-500 text-white rounded-lg cursor-pointer transition duration-300 hover:bg-red-600"
      >
        Clear List
      </button>
    </div>
  );
}
