export default function PackingList({
  items,
  onDeleteItem,
  onToggleItem,
  onEditItem,
  onClearList,
  filterCategory,
}) {
  const filteredItems =
    filterCategory === "All"
      ? items
      : items.filter((item) => item.category === filterCategory);

  return (
    <div className="packing-list w-full max-w-3xl mx-auto mt-8">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Packing List</h2>
      {filteredItems.length > 0 ? (
        <ul className="list-disc list-inside">
          {filteredItems.map((item) => (
            <li
              key={item.id}
              className="flex justify-between items-center mb-2"
            >
              <div className="flex items-center">
                <input
                  type="checkbox"
                  className="mr-2"
                  checked={item.packed}
                  onChange={() => onToggleItem(item.id)}
                />
                <span className={item.packed ? "line-through" : ""}>
                  {item.description} ({item.quantity})
                </span>
              </div>
              <div className="flex items-center">
                <button
                  className="ml-2 text-sm text-blue-500"
                  onClick={() =>
                    onEditItem(item.id, {
                      ...item,
                      description: prompt("Edit description", item.description),
                    })
                  }
                >
                  Edit
                </button>
                <button
                  className="ml-2 text-sm text-red-500"
                  onClick={() => onDeleteItem(item.id)}
                >
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-500">No items found.</p>
      )}
      <button
        className="mt-4 py-2 px-4 bg-red-500 text-white rounded-lg"
        onClick={onClearList}
      >
        Clear List
      </button>
    </div>
  );
}
