import { useMemo } from "react";

export default function Stats({ items }) {
  if (!items.length)
    return (
      <p className="text-lg text-gray-600">
        Start adding some items to your packing list! üöÄ
      </p>
    );

  const stats = useMemo(() => {
    const numItems = items.length;
    const numPacked = items.filter((item) => item.packed).length;
    const packedPercentage = Math.round((numPacked / numItems) * 100);
    const numUnpacked = numItems - numPacked;
    const itemsByCategory = items.reduce((acc, item) => {
      acc[item.category] = (acc[item.category] || 0) + 1;
      return acc;
    }, {});

    return {
      numItems,
      numPacked,
      numUnpacked,
      packedPercentage,
      itemsByCategory,
    };
  }, [items]);

  return (
    <footer className="stats w-full max-w-3xl bg-white p-8 rounded-lg shadow-lg mt-8">
      <div className="flex flex-wrap justify-between items-center mb-6">
        <div className="flex flex-col items-center">
          <h3 className="text-2xl font-bold text-gray-800">{stats.numItems}</h3>
          <p className="text-gray-600">Total Items</p>
        </div>
        <div className="flex flex-col items-center">
          <h3 className="text-2xl font-bold text-gray-800">
            {stats.numPacked}
          </h3>
          <p className="text-gray-600">Packed Items</p>
        </div>
        <div className="flex flex-col items-center">
          <h3 className="text-2xl font-bold text-gray-800">
            {stats.numUnpacked}
          </h3>
          <p className="text-gray-600">Unpacked Items</p>
        </div>
        <div className="flex flex-col items-center">
          <h3 className="text-2xl font-bold text-gray-800">
            {stats.packedPercentage}%
          </h3>
          <p className="text-gray-600">Packed Percentage</p>
        </div>
      </div>
      <div className="flex flex-wrap justify-between">
        {Object.keys(stats.itemsByCategory).map((category) => (
          <div key={category} className="flex flex-col items-center mb-4">
            <h4 className="text-xl font-semibold text-gray-700">{category}</h4>
            <p className="text-gray-500">
              {stats.itemsByCategory[category]} items
            </p>
          </div>
        ))}
      </div>
      <div className="mt-4 text-center">
        <em className="text-lg text-gray-600">
          {stats.packedPercentage === 100
            ? "You got everything! Ready to go! ‚úàÔ∏è"
            : "Keep packing! You‚Äôre almost there! üíº"}
        </em>
      </div>
    </footer>
  );
}
