import Item from "./Item";
import { useState } from "react";

export default function PackingList({
  items,
  onDeleteItem,
  onToggleItem,
  onEditItem,
  onClearList,
  categories,
}) {
  const [sortBy, setSortBy] = useState("input");
  const [searchTerm, setSearchTerm] = useState("");

  const sortedItems = items
    .filter((item) =>
      item.description.toLowerCase().includes(searchTerm.toLowerCase())
    )
    .sort((a, b) => {
      if (sortBy === "description") {
        return a.description.localeCompare(b.description);
      } else if (sortBy === "packed") {
        return Number(a.packed) - Number(b.packed);
      } else {
        return a.id - b.id;
      }
    });

  const categorizedItems = categories.map((category) => ({
    category,
    items: sortedItems.filter((item) => item.category === category),
  }));

  return (
    <div className="list w-full max-w-3xl bg-white p-8 rounded-lg shadow-lg mt-8">
      <input
        type="text"
        placeholder="Search items..."
        className="w-full py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select
        className="w-full py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
        value={sortBy}
        onChange={(e) => setSortBy(e.target.value)}
      >
        <option value="input">Sort by Input</option>
        <option value="description">Sort by Description</option>
        <option value="packed">Sort by Packed Status</option>
      </select>
      {categorizedItems.map(({ category, items }) => (
        <div key={category} className="mb-8">
          <h3 className="text-lg font-semibold mb-4">{category}</h3>
          <ul className="list-none">
            {items.map((item) => (
              <Item
                key={item.id}
                item={item}
                onDeleteItem={onDeleteItem}
                onToggleItem={onToggleItem}
                onEditItem={onEditItem}
              />
            ))}
          </ul>
        </div>
      ))}
      <button
        className="w-full py-2 px-4 bg-red-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-red-600 mt-4"
        onClick={onClearList}
      >
        Clear List
      </button>
    </div>
  );
}
