import React from "react";

export default function Modal({
  show,
  onClose,
  onConfirm,
  categoryToRemove,
  type,
}) {
  if (!show) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        {type === "category" ? (
          <>
            <h3 className="text-xl mb-4">Confirm Deletion</h3>
            <p className="mb-4">
              Are you sure you want to remove the category "{categoryToRemove}"?
            </p>
          </>
        ) : (
          <>
            <h3 className="text-xl mb-4">Confirm Clear List</h3>
            <p className="mb-4">
              Are you sure you want to clear the entire list?
            </p>
          </>
        )}
        <div className="flex justify-end gap-4">
          <button
            className="py-2 px-4 bg-gray-300 rounded-lg"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="py-2 px-4 bg-red-500 text-white rounded-lg"
            onClick={onConfirm}
          >
            {type === "category" ? "Delete" : "Clear"}
          </button>
        </div>
      </div>
    </div>
  );
}
