import { useState } from "react";

export default function Form({
  onAddItems,
  onAddCategory,
  onRemoveCategory,
  categories,
}) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [category, setCategory] = useState("General");
  const [newCategory, setNewCategory] = useState("");
  const [dueDate, setDueDate] = useState("");

  function handleSubmit(e) {
    e.preventDefault();

    if (!description) return;

    const newItem = {
      description,
      quantity,
      packed: false,
      id: Date.now(),
      category,
      dueDate,
    };

    onAddItems(newItem);

    setDescription("");
    setQuantity(1);
    setCategory("General");
    setDueDate("");
  }

  function handleAddNewCategory(e) {
    e.preventDefault();
    if (newCategory) {
      onAddCategory(newCategory);
      setCategory(newCategory);
      setNewCategory("");
    }
  }

  function handleRemoveCategory(e, categoryToRemove) {
    e.preventDefault();
    onRemoveCategory(categoryToRemove);
    if (category === categoryToRemove) {
      setCategory("General");
    }
  }

  return (
    <form
      className="add-form w-full max-w-xl bg-white p-8 rounded-lg shadow-md"
      onSubmit={handleSubmit}
    >
      <h3 className="text-xl font-semibold mb-4">
        What do you need for your üòç trip?
      </h3>
      <select
        className="w-full py-2 px-3 border border-gray-300 rounded-lg mb-4"
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        className="w-full py-2 px-3 border border-gray-300 rounded-lg mb-4"
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <div className="relative mb-4">
        <select
          className="w-full py-2 px-3 border border-gray-300 rounded-lg"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          {categories.map((cat) => (
            <option value={cat} key={cat}>
              {cat}
            </option>
          ))}
          <option value="add-new">Add New Category</option>
        </select>
        {category !== "General" && category !== "add-new" && (
          <button
            className="absolute right-2 top-2 py-1 px-2 bg-red-500 text-white rounded-lg text-sm"
            onClick={(e) => handleRemoveCategory(e, category)}
          >
            Remove
          </button>
        )}
      </div>
      {category === "add-new" && (
        <div className="flex items-center mb-4">
          <input
            className="w-full py-2 px-3 border border-gray-300 rounded-lg mr-2"
            type="text"
            placeholder="New Category"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
          />
          <button
            className="py-2 px-4 bg-blue-500 text-white rounded-lg transition duration-300 hover:bg-blue-600"
            onClick={handleAddNewCategory}
          >
            Add Category
          </button>
        </div>
      )}
      <input
        className="w-full py-2 px-3 border border-gray-300 rounded-lg mb-4"
        type="date"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
      />
      <button className="w-full py-2 px-4 bg-green-500 text-white rounded-lg transition duration-300 hover:bg-green-600">
        Add
      </button>
    </form>
  );
}
