import { useState } from "react";

export default function Form({ onAddItems, onAddCategory, categories }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [category, setCategory] = useState("General");
  const [newCategory, setNewCategory] = useState("");
  const [dueDate, setDueDate] = useState("");

  function handleSubmit(e) {
    e.preventDefault();

    if (!description) return;

    const newItem = {
      description,
      quantity,
      packed: false,
      id: Date.now(),
      category,
      dueDate,
    };

    onAddItems(newItem);

    setDescription("");
    setQuantity(1);
    setCategory("General");
    setDueDate("");
  }

  function handleAddNewCategory(e) {
    e.preventDefault();
    if (newCategory) {
      onAddCategory(newCategory);
      setCategory(newCategory);
      setNewCategory("");
    }
  }

  return (
    <form
      className="add-form w-full max-w-3xl bg-gray-100 p-8 flex flex-wrap gap-4 rounded-lg shadow-lg"
      onSubmit={handleSubmit}
    >
      <h3 className="w-full text-2xl text-gray-700 mb-4">
        What do you need for your üòç trip?
      </h3>
      <select
        className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
        value={quantity}
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
          <option value={num} key={num}>
            {num}
          </option>
        ))}
      </select>
      <input
        type="text"
        className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <select
        className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      >
        {categories.map((cat) => (
          <option value={cat} key={cat}>
            {cat}
          </option>
        ))}
        <option value="add-new">Add New Category</option>
      </select>
      {category === "add-new" && (
        <div className="w-full flex gap-4 mb-4">
          <input
            type="text"
            className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base"
            placeholder="New Category"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
          />
          <button
            className="py-2 px-4 bg-blue-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-blue-600"
            onClick={handleAddNewCategory}
          >
            Add Category
          </button>
        </div>
      )}
      <input
        type="date"
        className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
      />
      <button className="flex-1 py-2 px-4 bg-green-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-green-600">
        Add
      </button>
    </form>
  );
}
