import { useState, useEffect } from "react";
import Logo from "./Logo";
import Form from "./Form";
import PackingList from "./PackingList";
import Stats from "./Stats";
import Notification from "./Notification"; // Import Notification component

export default function App() {
  const [items, setItems] = useState(() => {
    const savedItems = localStorage.getItem("packingListItems");
    return savedItems ? JSON.parse(savedItems) : [];
  });

  const [notification, setNotification] = useState("");

  useEffect(() => {
    localStorage.setItem("packingListItems", JSON.stringify(items));
  }, [items]);

  function handleAddItems(item) {
    setItems((items) => [...items, item]);
    showNotification("Item added!");
  }

  function handleDeleteItem(id) {
    setItems((items) => items.filter((item) => item.id !== id));
    showNotification("Item deleted!");
  }

  function handleToggleItem(id) {
    setItems((items) =>
      items.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
    showNotification("Item status changed!");
  }

  function handleEditItem(id, newItem) {
    setItems((items) =>
      items.map((item) => (item.id === id ? { ...item, ...newItem } : item))
    );
    showNotification("Item edited!");
  }

  function handleClearList() {
    const confirmed = window.confirm(
      "Are you sure you want to delete all items?"
    );

    if (confirmed) {
      setItems([]);
      showNotification("All items cleared!");
    }
  }

  function showNotification(message) {
    setNotification(message);
    setTimeout(() => {
      setNotification("");
    }, 3000);
  }

  return (
    <div className="app">
      <Logo />
      <Form onAddItems={handleAddItems} />
      <PackingList
        items={items}
        onDeleteItem={handleDeleteItem}
        onToggleItem={handleToggleItem}
        onEditItem={handleEditItem}
        onClearList={handleClearList}
      />
      <Stats items={items} />
      {notification && <Notification message={notification} />}{" "}
      {/* Add Notification component */}
    </div>
  );
}
