import { useState } from "react";

export default function Form({
  onAddItems,
  onAddCategory,
  onEditCategory,
  onRemoveCategory,
  categories,
}) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [category, setCategory] = useState("General");
  const [newCategory, setNewCategory] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [editCategory, setEditCategory] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("General");

  function handleSubmit(e) {
    e.preventDefault();

    if (!description) return;

    const newItem = {
      description,
      quantity,
      packed: false,
      id: Date.now(),
      category,
      dueDate,
    };

    onAddItems(newItem);

    setDescription("");
    setQuantity(1);
    setCategory("General");
    setDueDate("");
  }

  function handleAddNewCategory(e) {
    e.preventDefault();
    if (newCategory) {
      onAddCategory(newCategory);
      setCategory(newCategory);
      setNewCategory("");
    }
  }

  function handleEditCategorySubmit(e) {
    e.preventDefault();
    if (editCategory && selectedCategory !== "General") {
      onEditCategory(selectedCategory, editCategory);
      setSelectedCategory(editCategory);
      setEditCategory("");
    }
  }

  function handleRemoveCategorySubmit(e) {
    e.preventDefault();
    if (selectedCategory !== "General") {
      onRemoveCategory(selectedCategory);
      setSelectedCategory("General");
    }
  }

  return (
    <form
      className="add-form w-full max-w-3xl bg-gray-100 p-8 flex flex-wrap gap-4 rounded-lg shadow-lg"
      onSubmit={handleSubmit}
    >
      <h3 className="w-full text-2xl text-gray-700 mb-4">
        What do you need for your üòç trip?
      </h3>
      <div className="flex-1 flex gap-4">
        <select
          className="py-2 px-4 border border-gray-300 rounded-lg text-base mb-4 w-24"
          value={quantity}
          onChange={(e) => setQuantity(Number(e.target.value))}
        >
          {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
            <option value={num} key={num}>
              {num}
            </option>
          ))}
        </select>
        <input
          type="text"
          className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
          placeholder="Item..."
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <select
        className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      >
        {categories.map((cat) => (
          <option value={cat} key={cat}>
            {cat}
          </option>
        ))}
        <option value="add-new">Add New Category</option>
      </select>
      {category === "add-new" && (
        <div className="w-full flex gap-4 mb-4">
          <input
            type="text"
            className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base"
            placeholder="New Category"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
          />
          <button
            onClick={handleAddNewCategory}
            className="py-2 px-4 bg-blue-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-blue-600"
          >
            Add Category
          </button>
        </div>
      )}
      <input
        type="date"
        className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base mb-4"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
      />
      <button className="flex-1 py-2 px-4 bg-green-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-green-600">
        Add
      </button>
      <div className="w-full mt-4 flex flex-col gap-4">
        <select
          className="py-2 px-4 border border-gray-300 rounded-lg text-base"
          value={selectedCategory}
          onChange={(e) => setSelectedCategory(e.target.value)}
        >
          {categories.map((cat) => (
            <option value={cat} key={cat}>
              {cat}
            </option>
          ))}
        </select>
        <div className="flex gap-4">
          <input
            type="text"
            className="flex-1 py-2 px-4 border border-gray-300 rounded-lg text-base"
            placeholder="Edit Category"
            value={editCategory}
            onChange={(e) => setEditCategory(e.target.value)}
          />
          <button
            onClick={handleEditCategorySubmit}
            className="py-2 px-4 bg-yellow-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-yellow-600"
          >
            Edit Category
          </button>
          <button
            onClick={handleRemoveCategorySubmit}
            className="py-2 px-4 bg-red-500 text-white rounded-lg text-base cursor-pointer transition duration-300 hover:bg-red-600"
          >
            Remove Category
          </button>
        </div>
      </div>
    </form>
  );
}
