import React from "react";
import PackingListItem from "./PackingListItem";

export default function PackingList({
  items,
  categories,
  onDeleteItem,
  onToggleItem,
  onEditItem,
  onClearList,
  selectedCategory,
  setSelectedCategory,
}) {
  return (
    <div className="packing-list w-full max-w-3xl mt-8">
      <div className="categories-container flex overflow-x-auto gap-4 mb-4">
        <button
          className={`py-2 px-4 rounded-lg ${
            selectedCategory === "All"
              ? "bg-blue-500 text-white"
              : "bg-gray-200"
          }`}
          onClick={() => setSelectedCategory("All")}
        >
          All
        </button>
        {categories.map((category) => (
          <button
            key={category}
            className={`py-2 px-4 rounded-lg ${
              selectedCategory === category
                ? "bg-blue-500 text-white"
                : "bg-gray-200"
            }`}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>
      {items
        .filter((item) =>
          selectedCategory === "All" ? true : item.category === selectedCategory
        )
        .map((item) => (
          <PackingListItem
            key={item.id}
            item={item}
            onDeleteItem={onDeleteItem}
            onToggleItem={onToggleItem}
            onEditItem={onEditItem}
          />
        ))}
      <button
        className="mt-4 py-2 px-4 bg-red-500 text-white rounded-lg transition duration-300 hover:bg-red-600"
        onClick={onClearList}
      >
        Clear List
      </button>
    </div>
  );
}
