import { useState } from "react";

export default function Item({ item, onDeleteItem, onToggleItem, onEditItem }) {
  const [isEditing, setIsEditing] = useState(false);
  const [description, setDescription] = useState(item.description);
  const [quantity, setQuantity] = useState(item.quantity);

  function handleEdit(e) {
    e.preventDefault();
    onEditItem(item.id, { description, quantity });
    setIsEditing(false);
  }

  return (
    <li className="flex items-center justify-between gap-4 p-4 bg-brown-600 rounded-lg shadow-md transition duration-300 ease-in-out hover:bg-brown-500">
      {isEditing ? (
        <form onSubmit={handleEdit} className="flex items-center gap-2">
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="border-b-2 border-yellow-300 focus:border-yellow-500 outline-none"
          />
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
            className="border-b-2 border-yellow-300 focus:border-yellow-500 outline-none"
          />
          <button type="submit" className="text-green-600">
            Save
          </button>
        </form>
      ) : (
        <>
          <input
            type="checkbox"
            checked={item.packed}
            onChange={() => onToggleItem(item.id)}
            className="w-5 h-5"
          />
          <span style={item.packed ? { textDecoration: "line-through" } : {}}>
            {item.quantity} {item.description}
          </span>
          <button onClick={() => setIsEditing(true)} className="text-blue-600">
            ✏️
          </button>
          <button
            onClick={() => onDeleteItem(item.id)}
            className="text-red-600"
          >
            ❌
          </button>
        </>
      )}
    </li>
  );
}
